package escape;

public class Escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 
		 이스케이프란 : 특수문자를 출력하기 위해서 사용하는것
		 \t = 탭
		 \n = 줄바꿈
		 \r = 캐리지리턴(\n과 같음 하지만 메모장에 할 때는 \r\n이 필요 즉 프로그램마다 다르다고 생각하면됨)
		 \" = "출력
		 \' = '출력
		 \\ = \출력
		 
		 */
		
		System.out.print("나는"+"\t"+"김태영"+"\n"+"\r"+"\""+"\'"+"\\");
		
		
	}

}
